mapper.select:
  select b.* from nf_rffssp r left join nf_busin b on r.id=b.rffsspid


          and b.status = #{status}


          and b.btype in

              #{a}



          and r.issueorg in

              #{b}



          and TO_DAYS(r.rec_time)=TO_DAYS(#{createTime})


          and r.apply_name like CONCAT(CONCAT('%',#{applyName}),'%')


          and r.rec_num like CONCAT(CONCAT('%',#{recNum}),'%')


          and r.storage_ent_name like CONCAT(CONCAT('%',#{storageEntName}),'%')


          and r.issueorg=#{orgCode}


order by b.lasttime desc

select  ad.code,ad.name, IFNULL((
select sum(o.num)
from nf_rffssp r
left join nf_inbound_and_outbound o on o.rffssp_id=r.id
left join nf_busin b on b.rffsspid=r.id
where r.storage_city=ad.code

    and o.types_of=#{typesOf}


    and date_format( o.operate_time, '%Y-%m-%d')  >=  #{startDate}


    and 	date_format( o.operate_time, '%Y-%m-%d') <= #{endDate}


    and 	o.type=#{type}

and b.status=22  and b.btype in (10,11,20,21)
), 0) as suma
from code_addiv ad
where ad.level=2
ORDER BY suma desc

select n.storage_name, n.rec_num, n.storage_amount, n.remarks,n.equipment_name_specification,n.apply_name,sheng.name as sheng,shi.name as shi ,xian. name as xian from nf_rffssp n LEFT JOIN code_addiv as sheng on n.storage_province=sheng.code LEFT JOIN code_addiv as shi on n.storage_city=shi.code LEFT JOIN code_addiv as xian on n.storage_county=xian.code LEFT JOIN nf_busin as b on b.rffsspid=n.id
and b.status = #{status}
and b.btype in
#{a}
and n.issueorg in
#{b}
and TO_DAYS(n.rec_time)=TO_DAYS(#{recTime})
order by b.lasttime desc

select b.*  from nf_busin b
left join nf_rffssp r on r.id=b.rffsspid


        and b.status = #{status}


        and b.btype in

            #{a}



        and r.issueorg in

            #{b}



        and r.rec_num like CONCAT(CONCAT('%',#{recNum}),'%')


        and r.storage_ent_name like CONCAT(CONCAT('%',#{storageEntName}),'%')


        and r.storage_rmount = #{storageAmount}


        and TO_DAYS(r.rec_time)>=TO_DAYS(#{recTimeBegin})


        and TO_DAYS(r.rec_time)<=TO_DAYS(#{recTimeEnd})


        and r.storage_province=#{storageProvince}


        and r.storage_city=#{storageCity}


        and r.storage_county=#{storageCounty}


order by b.lasttime desc

select (case when b.btype=10 then '备案' when b.btype=11 then '变更' when b.btype=12 then '注销' when b.btype=20 then '备案' when b.btype=21 then '变更' when b.btype=22 then '注销' else '' end) as typename, n.storage_ent_name, n.rec_num, n.storage_amount, n.rec_time, n.issueorg, org.name as orgname, sheng.name as sheng, shi.name as shi ,xian. name as xian from nf_rffssp n LEFT JOIN code_addiv as sheng on n.storage_province=sheng.code LEFT JOIN code_addiv as shi on n.storage_city=shi.code LEFT JOIN code_addiv as xian on n.storage_county=xian.code LEFT JOIN code_organ as org on n.issueorg=org.code LEFT JOIN nf_busin as b on b.rffsspid=n.id
and b.status = #{status}
and b.btype in
#{a}
and n.issueorg in
#{b}
and n.rec_num like CONCAT(CONCAT('%',#{recNum}),'%')
and n.storage_ent_name like CONCAT(CONCAT('%',#{storageEntName}),'%')
and n.storage_rmount = #{storageAmount}
and TO_DAYS(n.rec_time)>=TO_DAYS(#{recTimeBegin})
and TO_DAYS(n.rec_time)<=TO_DAYS(#{recTimeEnd})
and n.storage_province=#{storageProvince}
and n.storage_city=#{storageCity}
and n.storage_county=#{storageCounty}
order by b.lasttime desc

select r.apply_time, r.unisc_id, r.storage_ent_name, r.le_rep, r.contract_name, r.contract_tel, r.storage_name, sheng.name as storageprovname, shi.name as storagecityname ,xian. name as storagecountyname, r.storage_address, r.storage_power_ton, r.storage_power_cubic_meter, r.rec_num, r.rec_time from nf_rffssp r LEFT JOIN code_addiv as sheng on r.storage_province=sheng.code LEFT JOIN code_addiv as shi on r.storage_city=shi.code LEFT JOIN code_addiv as xian on r.storage_county=xian.code LEFT JOIN nf_busin as b on b.rffsspid=r.id where b.status = 22
and b.id=#{id}
order by b.lasttime desc
