mapper.select:
  select b.*  from nf_busin b
  left join nf_rffssp r on r.id=b.rffsspid


          and b.status = #{status}


          and b.btype in

              #{a}



          and r.issueorg in

              #{b}



          and TO_DAYS(b.createtime)=TO_DAYS(#{createTime})


order by b.lasttime desc

select  ad.code,ad.name, IFNULL((
select sum(o.num)
from nf_rffssp r
left join nf_inbound_and_outbound o on o.rffssp_id=r.id
left join nf_busin b on b.rffsspid=r.id
where r.storage_city=ad.code

    and o.types_of=#{typesOf}


    and date_format( o.operate_time, '%Y-%m-%d')  >=  #{startDate}


    and 	date_format( o.operate_time, '%Y-%m-%d') <= #{endDate}


    and 	o.type=#{type}

and b.status=22  and b.btype in (10,11,20,21)
), 0) as suma
from code_addiv ad
where ad.level=2
ORDER BY suma desc

select n.storage_name, n.rec_num, n.remarks,n.equipment_name_specification,n.apply_name,sheng.name as sheng,shi.name as shi ,xian. name as xian from nf_rffssp n LEFT JOIN code_addiv as sheng on n.storage_province=sheng.code LEFT JOIN code_addiv as shi on n.storage_city=shi.code LEFT JOIN code_addiv as xian on n.storage_county=xian.code LEFT JOIN nf_busin as b on b.rffsspid=n.id
and b.status = #{status}
and b.btype in
#{a}
and n.issueorg in
#{b}
and TO_DAYS(b.createtime)=TO_DAYS(#{createTime})
order by b.lasttime desc
